/* eslint-disable @stylistic/js/max-len */
import { Plugin } from 'vite';
import fs from 'node:fs';
import { resolve } from 'pathe';

const defaultContent = `
  <style>
    /* Reset b√°sico */
    * {
      margin: 0;
      padding: 0;
      box-sizing: border-box;
    }

    html, body {
      width: 100%;
      height: 100%;
      overflow: hidden;
    }

    body {
      display: flex;
      justify-content: center;
      align-items: center;
      background: linear-gradient(270deg, #262626, #0e2c3e, #2b2b2b); background-size: 600% 600%;  animation: backgroundAnimation 8s ease infinite;
    }

    @keyframes backgroundAnimation {
      0% { background-position: 0% 50%; }
      50% { background-position: 100% 50%; }
      100% { background-position: 0% 50%; }
    }

    .logo {
      width: 100px;
      height: auto;
      filter: drop-shadow(0 0 4px #aee6f3);
    }
  </style>
  <img class="logo" src="data:image/png;base64," alt="Logo">
`;

export default function loadingHTML(): Plugin {
  const virtualModuleId = 'virtual:loading-html';
  const resolvedVirtualModuleId = '\0' + virtualModuleId;
  let loadingHtmlContent = defaultContent;

  return {
    name: 'vite-plugin-loading-html',
    resolveId(id) {
      if (id === virtualModuleId) {
        return resolvedVirtualModuleId;
      }
    },
    load(id) {
      if (id === resolvedVirtualModuleId) {
        const loadingHtmlPath = resolve(process.cwd(), 'loading.html');

        if (fs.existsSync(loadingHtmlPath)) {
          loadingHtmlContent = fs.readFileSync(loadingHtmlPath, 'utf-8');
        }

        return `export default ${JSON.stringify(loadingHtmlContent)};`;
      }
    },
  };
}